mapper.select:
SELECT p.id AS post_id, p.title AS post_title, p.content AS post_content, p.view_count AS post_view_count, p.is_pinned AS post_is_pinned, p.created_by AS post_created_by, DATE_FORMAT(p.created_at, '%Y-%m-%d %H:%i:%s') AS post_created_at, c.name AS category_name
FROM announcement_posts p INNER JOIN announcement_categories c ON p.category_id = c.id
ORDER BY p.is_pinned DESC, ${sort} LIMIT #{pageSize} OFFSET #{offset}

SELECT p.id AS post_id, p.title AS post_title, p.content AS post_content, p.view_count AS post_view_count, p.is_pinned AS post_is_pinned, p.created_by AS post_created_by, DATE_FORMAT(p.created_at, '%Y-%m-%d %H:%i:%s') AS post_created_at, c.name AS category_name, p.user_id AS user_id
FROM announcement_posts p INNER JOIN announcement_categories c ON p.category_id = c.id
WHERE p.id = #{id}

mapper.sql:
WHERE 1 = 1


  AND c.id = #{categoryId}



  AND (
  p.title LIKE CONCAT('%', #{keyword}, '%')
  OR p.created_by LIKE CONCAT('%', #{keyword}, '%')
  OR p.content LIKE CONCAT('%', #{keyword}, '%')
  )



  AND p.created_at >= #{startDate}


  AND p.created_at <= #{endDate}
