mapper.select:
SELECT p.id AS post_id, p.title AS post_title, p.content AS post_content, p.view_count AS post_view_count, p.created_by AS post_created_by, DATE_FORMAT(p.created_at, '%Y-%m-%d %H:%i:%s') AS post_created_at, c.name AS category_name
FROM free_posts p INNER JOIN free_categories c ON p.category_id = c.id
ORDER BY ${searchCondition.sort}, p.id DESC LIMIT #{searchCondition.pageSize} OFFSET #{offset}

mapper.sql:
WHERE 1 = 1


  AND c.id = #{searchCondition.categoryId}



  AND (
  p.title LIKE CONCAT('%', #{searchCondition.keyword}, '%')
  OR p.created_by LIKE CONCAT('%', #{searchCondition.keyword}, '%')
  OR p.content LIKE CONCAT('%', #{searchCondition.keyword}, '%')
  )



  AND p.created_at >= #{searchCondition.startDate}


  AND p.created_at <= #{searchCondition.endDate}

mapper.update:
UPDATE
    free_posts
SET title       = #{title},
    content     = #{content},
    category_id = #{freePostCategory.id}
WHERE id = #{id}
